sys_script_include.script:
var validateEmailAddress2 = Class.create();
validateEmailAddress2.prototype = {
    initialize: function() {
    },

      //ALL YOUR METHODS DEFINED LIKE THIS
       isValid : function(emailStr) {
		emailStr = emailStr + '';
		// Compare emailStr against the allowed syntax as specified in the regular expression
		// If emailStr has allowed syntax, return true, else return false
		if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
			return true;
		}
		else {
		 return false;
		}
	   },

    type: 'validateEmailAddress2'
};
